* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    line-height: 1.6;
    color: #333;
    background-color: #f9f7f4;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

/* ========== NAVBAR ========== */
.navbar {
    background-color: #6f4e37;
    color: white;
    padding: 1rem 0;
    position: sticky;
    top: 0;
    z-index: 100;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}

.navbar .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.nav-brand h1 {
    font-size: 1.8rem;
    font-weight: bold;
}

.nav-menu {
    display: flex;
    list-style: none;
    gap: 2rem;
}

.nav-menu a {
    color: white;
    text-decoration: none;
    transition: color 0.3s ease;
    font-weight: 500;
}

.nav-menu a:hover,
.nav-menu a.active {
    color: #f4a460;
    border-bottom: 2px solid #f4a460;
    padding-bottom: 5px;
}

.hamburger {
    display: none;
    flex-direction: column;
    cursor: pointer;
}

.hamburger span {
    width: 25px;
    height: 3px;
    background-color: white;
    margin: 5px 0;
    transition: 0.3s;
}

/* ========== BUTTONS ========== */
.btn {
    display: inline-block;
    padding: 12px 30px;
    text-decoration: none;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    font-size: 1rem;
    transition: all 0.3s ease;
    text-align: center;
}

.btn-primary {
    background-color: #d2691e;
    color: white;
}

.btn-primary:hover {
    background-color: #8b4513;
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(0,0,0,0.2);
}

.btn-secondary {
    background-color: #f4a460;
    color: white;
}

.btn-secondary:hover {
    background-color: #d2691e;
}

/* ========== HERO SECTION ========== */
.hero {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 60px 20px;
    background: linear-gradient(135deg, #6f4e37 0%, #8b6f47 100%);
    color: white;
    min-height: 500px;
}

.hero-content {
    flex: 1;
    max-width: 500px;
}

.hero-content h2 {
    font-size: 3rem;
    margin-bottom: 20px;
    animation: slideInLeft 0.8s ease;
}

.hero-content p {
    font-size: 1.3rem;
    margin-bottom: 30px;
    color: #f4a460;
}

.hero-image {
    flex: 1;
    text-align: center;
}

.hero-image img {
    max-width: 100%;
    height: auto;
    border-radius: 10px;
    box-shadow: 0 10px 30px rgba(0,0,0,0.3);
}

/* ========== FEATURED SECTION ========== */
.featured {
    padding: 60px 20px;
    background-color: white;
}

.featured h2 {
    text-align: center;
    font-size: 2.5rem;
    margin-bottom: 50px;
    color: #6f4e37;
}

.featured-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 30px;
}

.card {
    background-color: #f9f7f4;
    border-radius: 10px;
    overflow: hidden;
    box-shadow: 0 5px 15px rgba(0,0,0,0.1);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.card:hover {
    transform: translateY(-10px);
    box-shadow: 0 10px 25px rgba(0,0,0,0.15);
}

.card img {
    width: 100%;
    height: 250px;
    object-fit: cover;
}

.card h3 {
    padding: 20px 20px 10px;
    color: #6f4e37;
    font-size: 1.5rem;
}

.card p {
    padding: 0 20px 20px;
    color: #666;
}

/* ========== WHY US SECTION ========== */
.why-us {
    padding: 60px 20px;
    background-color: #6f4e37;
    color: white;
}

.why-us h2 {
    text-align: center;
    font-size: 2.5rem;
    margin-bottom: 50px;
}

.features {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 30px;
}

.feature-item {
    text-align: center;
}

.feature-item i {
    font-size: 3rem;
    color: #f4a460;
    margin-bottom: 15px;
}

.feature-item h3 {
    margin-bottom: 10px;
    font-size: 1.3rem;
}

.feature-item p {
    color: #f0f0f0;
}

/* ========== MENU SECTION ========== */
.menu-section {
    padding: 60px 20px;
    background-color: white;
}

.menu-section h2 {
    text-align: center;
    font-size: 2.5rem;
    color: #6f4e37;
    margin-bottom: 10px;
}

.subtitle {
    text-align: center;
    color: #999;
    margin-bottom: 40px;
    font-size: 1.1rem;
}

.menu-filter {
    display: flex;
    justify-content: center;
    gap: 15px;
    margin-bottom: 40px;
    flex-wrap: wrap;
}

.filter-btn {
    padding: 10px 20px;
    border: 2px solid #6f4e37;
    background-color: white;
    color: #6f4e37;
    border-radius: 25px;
    cursor: pointer;
    font-size: 1rem;
    transition: all 0.3s ease;
}

.filter-btn:hover,
.filter-btn.active {
    background-color: #6f4e37;
    color: white;
}

.menu-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
    gap: 30px;
}

.menu-item {
    display: flex;
    background-color: #f9f7f4;
    border-radius: 10px;
    overflow: hidden;
    box-shadow: 0 3px 10px rgba(0,0,0,0.1);
    transition: transform 0.3s ease;
}

.menu-item:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 20px rgba(0,0,0,0.15);
}

.menu-image {
    width: 120px;
    min-width: 120px;
    height: 120px;
    overflow: hidden;
}

.menu-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.menu-details {
    flex: 1;
    padding: 20px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.menu-details h3 {
    color: #6f4e37;
    margin-bottom: 8px;
    font-size: 1.2rem;
}

.menu-details p {
    color: #666;
    font-size: 0.9rem;
    margin-bottom: 15px;
    flex-grow: 1;
}

.menu-price {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.price {
    font-weight: bold;
    color: #d2691e;
    font-size: 1.2rem;
}

.menu-price small {
    color: #999;
    font-size: 0.85rem;
}

/* ========== CONTACT SECTION ========== */
.contact-section {
    padding: 60px 20px;
    background-color: white;
}

.contact-section h2 {
    text-align: center;
    font-size: 2.5rem;
    color: #6f4e37;
    margin-bottom: 50px;
}

.contact-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 40px;
    margin-bottom: 60px;
}

.contact-info h3,
.contact-form-wrapper h3 {
    color: #6f4e37;
    margin-bottom: 30px;
    font-size: 1.5rem;
}

.info-item {
    display: flex;
    gap: 20px;
    margin-bottom: 25px;
}

.info-item i {
    font-size: 1.5rem;
    color: #d2691e;
    min-width: 30px;
}

.info-item h4 {
    color: #6f4e37;
    margin-bottom: 5px;
}

.info-item p {
    color: #666;
}

.info-item a {
    color: #d2691e;
    text-decoration: none;
}

.info-item a:hover {
    text-decoration: underline;
}

.social-links {
    display: flex;
    gap: 15px;
    margin-top: 30px;
}

.social-links a {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    background-color: #6f4e37;
    color: white;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    transition: all 0.3s ease;
}

.social-links a:hover {
    background-color: #d2691e;
    transform: translateY(-5px);
}

/* ========== CONTACT FORM ========== */
.contact-form {
    display: flex;
    flex-direction: column;
}

.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    color: #6f4e37;
    font-weight: 500;
}

.form-group input,
.form-group textarea {
    width: 100%;
    padding: 12px;
    border: 2px solid #e0e0e0;
    border-radius: 5px;
    font-family: inherit;
    font-size: 1rem;
    transition: border-color 0.3s ease;
}

.form-group input:focus,
.form-group textarea:focus {
    outline: none;
    border-color: #d2691e;
    box-shadow: 0 0 5px rgba(210, 105, 30, 0.3);
}

.contact-form button {
    align-self: flex-start;
}

/* ========== MAP SECTION ========== */
.map-container {
    margin-top: 60px;
}

.map-container h3 {
    color: #6f4e37;
    margin-bottom: 20px;
    font-size: 1.5rem;
}

.map-container iframe {
    border-radius: 10px;
    box-shadow: 0 5px 15px rgba(0,0,0,0.1);
}

/* ========== CTA SECTION ========== */
.cta {
    background: linear-gradient(135deg, #6f4e37 0%, #8b6f47 100%);
    color: white;
    padding: 60px 20px;
    text-align: center;
}

.cta h2 {
    font-size: 2.5rem;
    margin-bottom: 20px;
}

.cta p {
    font-size: 1.2rem;
    margin-bottom: 30px;
    color: #f4a460;
}

/* ========== ABOUT SECTION ========== */
.about-section {
    padding: 60px 20px;
    background-color: white;
}

.about-section h2 {
    text-align: center;
    font-size: 2.5rem;
    color: #6f4e37;
    margin-bottom: 50px;
}

.about-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 40px;
    margin-bottom: 60px;
    align-items: center;
}

.about-text h3 {
    color: #6f4e37;
    margin: 30px 0 15px;
    font-size: 1.5rem;
}

.about-text h3:first-child {
    margin-top: 0;
}

.about-text p {
    color: #666;
    margin-bottom: 15px;
    text-align: justify;
}

.about-text ul {
    list-style-position: inside;
    color: #666;
}

.about-text li {
    margin-bottom: 10px;
}

.about-image img {
    width: 100%;
    border-radius: 10px;
    box-shadow: 0 10px 30px rgba(0,0,0,0.2);
}

.team-section {
    text-align: center;
    padding: 40px 0;
    border-top: 2px solid #f0f0f0;
}

.team-section h3 {
    color: #6f4e37;
    font-size: 1.8rem;
    margin-bottom: 15px;
}

.team-section p {
    color: #999;
    margin-bottom: 30px;
}

/* ========== FOOTER ========== */
.footer {
    background-color: #6f4e37;
    color: white;
    padding: 50px 20px 20px;
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 30px;
    margin-bottom: 30px;
}

.footer-section h3 {
    color: #f4a460;
    margin-bottom: 15px;
}

.footer-section p {
    color: #f0f0f0;
    margin-bottom: 8px;
}

.footer-section a {
    color: #f4a460;
    text-decoration: none;
    transition: color 0.3s ease;
}

.footer-section a:hover {
    color: white;
}

.footer-bottom {
    text-align: center;
    padding-top: 20px;
    border-top: 1px solid rgba(255,255,255,0.1);
    color: #ccc;
}

/* ========== ANIMATIONS ========== */
@keyframes slideInLeft {
    from {
        opacity: 0;
        transform: translateX(-50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes slideInRight {
    from {
        opacity: 0;
        transform: translateX(50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

