        flex-direction: column;
        background-color: #6f4e37;
        width: 100%;
        padding: 20px;
        gap: 0;
        display: none;
    }

    .nav-menu.active {
        display: flex;
    }

    .nav-menu a {
        padding: 10px 0;
    }

    .nav-brand h1 {
        font-size: 1.3rem;
    }

    .hero-content h2 {
        font-size: 1.5rem;
    }

    .hero-content p {
        font-size: 1rem;
    }

    .featured h2,
    .menu-section h2,
    .contact-section h2,
    .about-section h2,
    .why-us h2,
    .cta h2 {
        font-size: 1.8rem;
    }

    .featured-grid,
    .menu-grid {
        grid-template-columns: 1fr;
    }

    .features {
        grid-template-columns: 1fr;
    }

    .menu-filter {
        gap: 10px;
    }

    .filter-btn {
        padding: 8px 15px;
        font-size: 0.9rem;
    }

    .contact-form button {
        width: 100%;
    }

    .footer-content {
        grid-template-columns: 1fr;
    }

    .cta p {
        font-size: 1rem;
    }

    .social-links {
        justify-content: center;
    }
}

